const slider=document.querySelectorAll(".home-slide"),point=document.querySelectorAll(".pointer");var a=0;const autoslider=()=>{a<slider.length-1?a++:a=0;for(var e=0;e<slider.length;e++)slider[e].classList.remove("active"),point[e].classList.remove("pointerup");slider[a].classList.add("active"),point[a].classList.add("pointerup")};setInterval(autoslider,5e3);var indexnumber,arrycontainer=[];const pointerclick=()=>{for(var e=0;e<point.length;e++){arrycontainer.push(point[e].innerHTML);point[e].onclick=function(){for(var e=0;e<point.length;e++)slider[e].classList.remove("active"),point[e].classList.remove("pointerup");indexnumber=arrycontainer.indexOf(this.innerHTML),slider[indexnumber].classList.add("active"),point[indexnumber].classList.add("pointerup")}}};pointerclick();var countespan=document.querySelectorAll(".counter");countespan.forEach(e=>{e.innerHTML=0;var t=()=>{var n=Number(e.getAttribute("data-target")),r=Number(e.innerHTML),i=n/20;r<n&&(e.innerHTML=`${i+r}`,setTimeout(t,100))};t()});const btnmenu=document.querySelectorAll(".btn-manu"),siam=()=>{document.querySelector(".navbar-nav").classList.toggle("manu-show-drope")};